<?php/** * Content not found. Display helpful error message. * * @since Hermi * @since Hermi 0.1.0 */  if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}?><?php do_action( 'hermi_entry_before' ); ?><article id="post-0" <?php post_class( [ 'hentry', 'not-found' ] ); ?>>	<?php do_action( 'hermi_entry_top' ); ?>		<header class="entry-header">		<div class="entry-title-container grid-container">			<div class="grid-x">				<div class="cell small-12">					<h1 class="entry-title"><?php _e( 'Not Found.', 'hermi' ); ?></h1>				</div>			</div>		</div>	</header><!-- .entry-header -->		<?php do_action( 'hermi_entry_content_before' ); ?>	<div class="entry-content">		<div class="grid-x">			<div class="cell small-12">				<p class="content-not-found-message">					<?php						do_action( 'hermi_entry_content_top' );						printf( '%1$s<strong><em><code>%2$s</code></em></strong>%3$s',							__( 'Sorry, but the page ', 'hermi' ),							esc_url( $_SERVER['REDIRECT_URL'] ),							__( ' does not exist.', 'hermi' )						);												do_action( 'hermi_entry_content_bottom' );					?>				</p>			</div>		</div>	</div><!-- .entry-content -->	<?php do_action( 'hermi_entry_content_after' ); ?>		<footer class="entry-footer">		<div class="entry-not-found-search-wrap">			<div class="grid-x">				<div class="cell small-12">					<p>						<?php get_search_form(); // Loads the searchform.php template. ?>					</p>				</div>			</div>		</div>		</footer><!-- .entry-footer -->		<?php do_action( 'hermi_entry_bottom' ); ?>	</article><!-- #post-0 --><?php do_action( 'hermi_entry_after' ); ?>