<?php/** * Template part for displaying pagination for post archives. * * @package Hermi * @since Hermi 0.1.0 */if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}// Bail if this is the only page of results.if ( $GLOBALS['wp_query']->max_num_pages <= 1 ) {	return;}?><div class="archive-pagination-wrap pagination-wrap">	<?php		// Show both types of pagination (visibility handled via CSS)		// Page number pagination. E.g.:  < 1 2 3...7 8 9 >		get_template_part( 'template-parts/pagination/pagination-archive-page-numbers' );		// Next/Previous pagination		get_template_part( 'template-parts/pagination/pagination-archive-next-previous' );	?></div><!-- .archive-pagination-wrap -->