<?php/** * Template part for displaying the loop on archive pages. * * @package Hermi * @since Hermi 0.1.0 */if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}?><?php do_action( 'hermi_content_before' ); ?><main id="main-content" class="site-main">	<?php		do_action( 'hermi_content_top' );		if ( have_posts() ) {			do_action( 'hermi_content_while_before' );			while ( have_posts() ) {				the_post();				get_template_part( 'template-parts/post/content', get_post_format() );			}			do_action( 'hermi_content_while_after' );		} else if ( is_search() ) {				get_template_part( 'template-parts/search/content-search-empty' );		} else {				get_template_part( 'template-parts/error/content-error' );		}		do_action( 'hermi_content_bottom' );		do_action( 'hermi_content_bottom_archive' );	?></main><!-- .main-content --><?php do_action( 'hermi_content_after' ); ?>