<?php /** * Template part for displaying pagination for post archives. * * @package Hermi * @since Hermi 0.1.0 */  if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}if ( $GLOBALS['wp_query']->max_num_pages <= 1 ) {	return;}	?><div class="archive-pagination-wrap pagination-wrap">	<?php get_template_part( 'templates/pagination/pagination-archive-page-numbers' ); ?>	<?php get_template_part( 'templates/pagination/pagination-archive-next-previous' ); ?></div><!-- .archive-pagination-wrap -->