<?php/** * Template part for displaying pagination for post archives. * * @package Hermi * @since Hermi 0.1.0 */if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}// Bail if this is the only page of results.if ( $GLOBALS['wp_query']->max_num_pages <= 1 ) {	return;}?><nav class="pagination-archive">	<?php		/* Show both page numbers (larger screens) and link pagination (smaller screens).			 Visibility is handled using styles. */				// Page number pagination e.g. < 1 2 3...7 8 9 >		get_template_part( 'templates/parts/pagination/pagination-archive-numbers' );		// Links pagination e.g. Next/Previous		get_template_part( 'templates/parts/pagination/pagination-archive-links' );	?></nav><!-- .pagination-archive -->