<?php/** * Template part for displaying pagination for post archives. * * @package Hermi * @since Hermi 0.1.0 */if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}// Bail if this is the only page of results.if ( $GLOBALS['wp_query']->max_num_pages <= 1 ) {	return;}?><nav class="pagination-archive">	<?php		// Show both types of pagination (visibility handled using styles).		// Page number pagination. E.g.:  < 1 2 3...7 8 9 >		get_template_part( 'templates/parts/pagination/pagination-archive-page-numbers-container' );		// Next/Previous pagination		get_template_part( 'templates/parts/pagination/pagination-archive-links-container' );	?></nav><!-- .archive-pagination -->