<?php /** * Template part for displaying the loop on archive pages. * * @package Hermi * @since Hermi 0.1.0  */  if ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}?><?php do_action( 'hermi_content_before' ); ?><main id="main-content" class="site-main">	<?php		do_action( 'hermi_content_top' );				get_template_part( 'templates/post/archive/heading-archive' );		do_action( 'hermi_archives_content_top_type-post' );				if ( have_posts() ) {						do_action( 'hermi_content_while_before' );			while ( have_posts() ) {				the_post();				get_template_part( 'templates/post/format', hermi_get_post_format_name() );			}			do_action( 'hermi_content_while_after' );					} else if ( is_search() ) {				get_template_part( 'templates/search/content-search-empty' );		} else {				get_template_part( 'templates/error/content-error' );		}		get_template_part( 'templates/pagination/pagination-archive' );		do_action( 'hermi_archives_content_bottom_type-post' );				do_action( 'hermi_content_bottom' );	?></main><!-- .main-content --><?php do_action( 'hermi_content_after' ); ?>